function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   if(newValue!=''){
	g_form.setSectionDisplay('mobile_details',true);
   g_form.setSectionDisplay('delivery_details',true);

   g_form.getReference('u_reference_1',getDetails);
   }

   //Type appropriate comment here, and begin script below

}
function getDetails(value){
	g_form.setValue('u_string_5',value.u_choice_2);
	g_form.setValue('u_price_7',value.u_price_3);
	g_form.setValue('u_string_6',value.short_description);
	g_form.setValue('u_glide_date_8',value.warranty_expiration);


}
